package CoreJava;

public class ElseifLadder {
	
	public static void elseifladdermethod() {
		
		if(1<2) {
			
			System.out.println("If block is executing");
		}else if(10>20) {
			
			System.out.println("else If block is executing");

		}else {
			
			System.out.println("Else block is executing");
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		elseifladdermethod();
	}

}
